from pystratum.BulkHandler import BulkHandler


class TestBulkHandler(BulkHandler):
    # ------------------------------------------------------------------------------------------------------------------
    def __init__(self):
        """
        Object constructor.
        """
        self.count = 0

    # ------------------------------------------------------------------------------------------------------------------
    def row(self, row):
        self.count += 1

        print(self.count)

    # ------------------------------------------------------------------------------------------------------------------
    def start(self):
        self.count = 0
        print('start')

    # ------------------------------------------------------------------------------------------------------------------
    def stop(self):
        print('stop')

# ----------------------------------------------------------------------------------------------------------------------
